import React from "react";
import {
  GoogleMap,
  useLoadScript,
  Marker,
  InfoWindow,
} from "@react-google-maps/api";
import "@reach/combobox/styles.css";
import {
  Combobox,
  ComboboxInput,
  ComboboxPopover,
  ComboboxList,
  ComboboxOption,
  ComboboxOptionText,
} from "@reach/combobox";

import mapStyles from "./mapStyles";
import { useState, useEffect, useCallback, useRef } from "react";
import usePlacesAutocomplete, {
  getGeocode,
  getLatLng,
} from "use-places-autocomplete";


const libraries = ["places"];
const mapContainerStyle = {
  width: "100vw",
  height: "100vh",
};

const options = {
  styles: mapStyles,
  disableDefaultUI: true,
};

export default function App() {
  const [userLocation, setUserLocation] = useState(null);
  const getGeoLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition((position) => {
        const { latitude, longitude } = position.coords;
        setUserLocation({
          lat: latitude,
          lng: longitude,
        });
      });
    }
  };

  useEffect(() => {
    getGeoLocation();
  }, []);
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
    libraries,
  });
  const [markers, setMarkers] = useState([]);
  const [selected, setSelected] = useState(null);

  const onMapClick = useCallback((event) => {
    setMarkers((current) => [
      ...current,
      {
        lat: event.latLng.lat(),
        lng: event.latLng.lng(),
        time: new Date(),
      },
    ]);
  }, [])

  const mapRef = useRef();
  const onMapLoad = useCallback((map) => {
    mapRef.current = map;
  }, []);

  if (loadError) return "error loading maps";
  if (!isLoaded) return "loading maps";

  return (
    <div>
      <GoogleMap
        mapContainerStyle={mapContainerStyle}
        zoom={18}
        center={userLocation ? userLocation : { lat: -34.397, lng: 150.644 }}
        options={options}
        onClick={(event) => {
          onMapClick(event);
        } }
      >
        {markers.map((marker) => (
          <Marker
            key={marker.time.toISOString()}
            position={{ lat: marker.lat, lng: marker.lng }}
            icon={{
              url: "https://img.icons8.com/color/48/000000/marker.png",
              scaledSize: new window.google.maps.Size(30, 30),
              origin: new window.google.maps.Point(0, 0),
              anchor: new window.google.maps.Point(15, 15),
            }}
            onClick={() => {
              setSelected(marker);
            } }
          />
        ))}
        {
          selected ? (
            <InfoWindow
              position={{ lat: selected.lat, lng: selected.lng }}
              onCloseClick={() => {
                setSelected(null);
              } }
            >
              <div>
                <h2>INFOBOXXXX</h2>
                <p>CAIXA DE SELEÇAO TESTE</p>
                <p>
                  <small>
                    Coord: <br />
                    {selected.lat} - {selected.lng}
                  </small>
                </p>
                <p>
                  <small>
                    <em>{selected.time.toLocaleString()}</em>
                  </small>
                </p>                            
              </div>
            </InfoWindow>
          ): null
        }
      </GoogleMap>
    </div>
  );
}

function Search() {
  const {} = usePlacesAutocomplete({
    requestOptions: {
      location: { lat: () => -34.397, lng: () => 150.644 },
      radius: 200 * 1000,
    },
  });
  return (
    <div>
      <Combobox onSelect={onSelect}>
        <ComboboxInput
          value={value}
          onChange={(e) => {
            setValue(e.target.value);
          }
          }
          placeholder="Search places..."
        />
        <ComboboxPopover>
          <ComboboxList>
            {getSuggestions(value).map((suggestion) => {
              const {
                id,
                description,
                placeId,
              } = suggestion;
              return (
                <ComboboxOption
                  key={id}
                  value={description}
                  onSelect={() => {
                    fetchPlace(placeId);
                  } }
                >
                  {description}
                </ComboboxOption>
              );
            } )}
          </ComboboxList>
        </ComboboxPopover>
      </Combobox>
    </div>
  );
}
